import { useState } from "react";
import Style from './SiginUp-module.css'

const SiginUp = () => {

    //----------Local States-------------------------------------//

    const [userInfo, setUserInfo] = useState({
        name: "",
        email: "",
        password: "",
    });

    const [errors, setErrors] = useState({
        name: "",
        email: "",
        password: ""
    });


    //------------Handlers---------------------------------//

    const handleChange = (event)=> {
        setUserInfo({
            ...userInfo,
            [event.target.name]: event.target.value});
    };

    const handleSubmit = (event) => {
        event.preventDefault()

    }

    return (
        <div className={Style}>
            <form onSubmit={handleSubmit}> 

                <label htmlFor="Name">Enter your Name</label>
                <input name="Name" type="text" placeholder="Name" value={userInfo.name} onChange={handleChange}/>

                <label htmlFor="Email">Enter Your Email</label>
                <input name="Email" type="email" placeholder="E-mail" value={userInfo.email} onChange={handleChange}/>

                <label htmlFor="Password">Enter your Password</label>
                <input name="Password" type="password" value={userInfo.password} onChange={handleChange} />

                <button type="submit" >Submit</button>
            </form>
        </div>
    )
}

export default SiginUp;
