import { useState } from "react";
import style from './SiginUp.module.css'


const SignUp = (props) => {

    //----------Local States-------------------------------------//

    const [userInfo, setUserInfo] = useState({
        name: "",
        email: "",
        password: "",
    });

   /* const [errors, setErrors] = useState({
        name: "",
        email: "",
        password: ""
    });*/


    //------------Handlers---------------------------------//

    const handleChange = (event)=> {
        setUserInfo({
            ...userInfo,
            [event.target.name]: event.target.value});
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        props.signUp(userInfo)
    }

    return (
        <div >
        <img className={style.contain} src={Rick_and_Morty} alt="Rick And Morty" />
    <div>
        <form className={style.form} onSubmit={handleSubmit}>
          <div className={style.img}></div>

          <label htmlFor="name">Name</label>
          <input name="name" type="text" placeholder="Name" value={userInfo.name}/>


          <label htmlFor="email">Email</label>
          <input name="email" type="email" placeholder="E-mail" value={userInfo.email} onChange={handleChange}/>
        <p className={style.errors}>{errors.email}</p>


          <label htmlFor="password" >Password</label>
          <input name="password" type="password" placeholder="Password" value={userInfo.password} onChange={handleChange}/>
          <p className={style.errors}>{errors.password}</p>

          <button className={style.formButton} type="submit" disabled={!userInfo.email || !userInfo.password || errors.email.length >1 || errors.password.length > 1}>Acceder</button>

          <p>you have an account? log in</p>
          <NavLink to="/" ><button className={style.formButton}>Sign Up</button></NavLink> 
        </form>
    </div>

    </div>
    )
}

export default SignUp;
